import React, {Component} from "react";
import Popup from './popup'
import axios from 'axios';

class SearchPage extends Component {
    constructor(props){
        super(props)
        this.state={
            ticker:'',
            getInfo:false,
            symbol:''  
        }
        this.handleInput= this.handleInput.bind(this)
    }

    handleInput= (event) => {
        this.setState({
            ticker: event.target.value
        });
    }

    findStock = async (e)=>{
        e.preventDefault();
        console.log('ticker ',this.state.ticker)
        const config ={ 
            method: 'get',
            url: 'https://api.iextrading.com/1.0/tops?symbols='+this.state.ticker
        }

        try{
            let res=await axios(config)
            console.log(res.data[0].symbol)
            this.setState({getInfo:true,symbol:res.data[0].symbol})
            console.log('getInfor',this.state.getInfo)
            alert(this.state.getInfo)
        }catch(err){
            console.log('getInfor',this.state.getInfo)
            this.setState({getInfo:true,symbol:''})
            alert('No Stock Found')
        }
    }

    render(){
                return(
                    <div>
                    <form>
                    <input type="text" placeholder="Search.." name="search" onChange={this.handleInput} />
                    <button type="submit" onClick={this.findStock}/>
                    </form>
                    <Popup symbol={this.state.symbol} pop={true}/>
                    </div>
                )
    }
}

export default SearchPage;
