import React, { Component } from 'react';
//import Error from './Error';

class Register extends Component{

    constructor(props){
        super(props);
        this.state = {
            firstName:"",
            lastName:"",
            email:"",
            password:"",
            confirmPassword:"",
            count:0,
            error:""
        }

        this.handleInput= this.handleInput.bind(this)
    }

    handleInput= (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    submit = (e)=>{
        e.preventDefault();
        let err = new Map();
        if(this.state.firstName===null || this.state.firstName.length<2){
            err["firstName"]= {
                error:true,
                msg:"First name is not valid"
            }
            this.setState({error:err})
        }
    }


    render(){
        if(this.state.submit===1){
            return (
                <form>
                    <label>
                    First Name:
                    {this.state.error["firstName"].error ? this.state.error["firstName"].msg : ""}
                    <br/>
                    <input type="text" classname={this.state.error["firstName"].error ? "error" : ""} name="firstName" required onChange={this.handleInput}/>
                    <br/>
                    Last Name:
                    <br/>
                    <input type="text" name="lastName" required onChange={this.handleInput}/>
                    <br/>
                    Email:
                    <br/>
                    <input type="email" name="email" required onChange={this.handleInput}/>
                    <br/>
                    Password:
                    <br/>
                    <input type="password" name="password" required onChange={this.handleInput}/>
                    <br/>
                    Confirm Password:
                    <br/>
                    <input type="password" name="confirmPassword" required onChange={this.handleInput}/>
                    </label>
                    <br/>
                    <input type="submit" value="Submit" required onClick={this.submit}/>
                </form>
            )
        }
        else{
            return(
                    <form>
                    <label>
                    First Name:
                    <br/>
                    <input type="text" name="firstName" required onChange={this.handleInput}/>
                    <br/>
                    Last Name:
                    <br/>
                    <input type="text" name="lastName" required onChange={this.handleInput}/>
                    <br/>
                    Email:
                    <br/>
                    <input type="email" name="email" required onChange={this.handleInput}/>
                    <br/>
                    Password:
                    <br/>
                    <input type="password" name="password" required onChange={this.handleInput}/>
                    <br/>
                    Confirm Password:
                    <br/>
                    <input type="password" name="confirmPassword" required onChange={this.handleInput}/>
                    </label>
                    <br/>
                    <input type="submit" value="Submit" required onClick={this.submit}/>
                    </form>
        )

    }
}

}

export default Register;
