import React, { Component } from 'react';
import validate from 'validator';


    function error(msg){
        return {
            error:true,
            msg:msg
        }
    }

    function valid(){
        return {
            error:false,
            msg:""
        }
    }

    function validateName(name){
        validate.validators.presence.message = "is required";
        let result = validate({name:name}, {input: {presence: {allowEmpty: false}}});
        return result;
    }

    function validateEmail(email){
        let constraints = {
            from: {
              email: {
                message: "Invalid Email"
              }
            }
        };

        let result=validator({from: email}, constraints)
        return result;
    }

    function validatePassword(password){
        let regex='^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$'
        if(regex.test(password)===false){
            return this.error("Password does not meet criteria")
        }
        else{
            return this.valid()
        }
    }

    function validateConfirmPassword(password,confirmPassword){
        if(password!=confirmPassword){
            return this.error("Passwords do not match")
        }
        else{
            return this.valid()
        }
    }

    export function validateForm(fieldName,field){  
        switch(fieldName,field) {
            case "firstName":
                this.validateName(field)
                break;
            case "lastName":
                this.validateName(field)
                break;
            case "email":
                this.validateEmail(field)
                break;
            case "password":
                this.validatePassword(field)
                break;
            case "confirmPassword":
                this.validateConfirmPassword(field)
                break;
            default:
                return this.valid()
        }
    }
