import React, { Component } from 'react';
import {validateForm} from './validate';
import '../styles/Register.css';
import axios from 'axios'
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles(theme => ({
    '@global': {
      body: {
        backgroundColor: theme.palette.common.white,
      },
    },
    paper: {
      marginTop: theme.spacing(8),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(3),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
  }));

class Register extends Component{

    constructor(props){
        super(props);
        this.state = {
            firstName:"",
            lastName:"",
            email:"",
            password:"",
            confirmPassword:"",
            invalid:false,
            error:""
        }

        this.handleInput= this.handleInput.bind(this)
        this.registerUser = this.registerUser.bind(this)
    }


    handleInput= (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }


     registerUser= async ()=>{

        const config ={ 
            method: 'post',
            url: 'http://localhost:8080/register',
            data: {
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                email:this.state.email,
                password:this.state.password,
                confirmPassword:this.state.confirmPassword
            }
        }

        let res = await axios(config)
        console.log(res.data.msg)
        return res;
    }

    submit = async (e)=>{
        e.preventDefault();
        let err = new Map();
        let invalid=false;
        const keys = Object.keys(this.state)
        for (const key of keys) {
            if(key!=="error" || key!=="count"){
                let result=await validateForm(key,this.state[key])
                err[key]=result
                if(result.error===true){
                    invalid=true
                }
            }
        }
        console.log(err)
        this.setState({error:err,invalid})
        if(this.state.invalid===false){
            let result=await this.registerUser()
            alert(result.data.msg)
        }

    }


    render(){
        const classes = useStyles();
        if(this.state.invalid===true){
            console.log('error render')
            return (
                <form>
                    <label>
                    First Name:
                    <br/>
                    <input type="text" className={this.state.error["firstName"].error ? "error" : ""} name="firstName" required onChange={this.handleInput}/>
                    <br/>
                    <div class="error">{this.state.error["firstName"].error ? this.state.error["firstName"].msg : ""}</div>
                    Last Name:
                    <br/>
                    <input type="text" name="lastName" className={this.state.error["lastName"].error ? "error" : ""} required onChange={this.handleInput}/>
                    <div class="error">{this.state.error["lastName"].error ? this.state.error["lastName"].msg : ""}</div>
                    Email:
                    <br/>
                    <input type="email" name="email" className={this.state.error["email"].error ? "error" : ""}  required onChange={this.handleInput}/>
                    <div class="error">{this.state.error["email"].error ? this.state.error["email"].msg : ""}</div>
                    Password:
                    <br/>
                    <input type="password" name="password" className={this.state.error["password"].error ? "error" : ""} required onChange={this.handleInput}/>
                    <div class="error">{this.state.error["password"].error ? this.state.error["password"].msg : ""}</div>
                    </label>
                    <br/>
                    <input type="submit" value="Submit" required onClick={this.submit}/>
                </form>
            )
        }
        else{
            return(
                <Container component="main" maxWidth="xs">
                <CssBaseline />
                <div className={classes.paper}>
                  <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                  </Avatar>
                  <Typography component="h1" variant="h5">
                    Sign up
                  </Typography>
                  <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          autoComplete="fname"
                          name="firstName"
                          variant="outlined"
                          required
                          fullWidth
                          id="firstName"
                          label="First Name"
                          autoFocus
                        />
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          variant="outlined"
                          required
                          fullWidth
                          id="lastName"
                          label="Last Name"
                          name="lastName"
                          autoComplete="lname"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          variant="outlined"
                          required
                          fullWidth
                          id="email"
                          label="Email Address"
                          name="email"
                          autoComplete="email"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          variant="outlined"
                          required
                          fullWidth
                          name="password"
                          label="Password"
                          type="password"
                          id="password"
                          autoComplete="current-password"
                        />
                      </Grid>
                    </Grid>
                    <Button
                      type="submit"
                      fullWidth
                      variant="contained"
                      color="primary"
                      className={classes.submit}
                    >
                      Sign Up
                    </Button>
                    <Grid container justify="flex-end">
                      <Grid item>
                        <Link href="#" variant="body2">
                          Already have an account? Sign in
                        </Link>
                      </Grid>
                    </Grid>
                  </form>
                </div>
              </Container>
            )

    }
}

}

export default Register;
